async function a(s){try{const r=s.replace(/-/g,"");if(!/^\d{7}$/.test(r))return{success:!1,message:"郵便番号は7桁の数字で入力してください"};const t=await fetch(`https://zipcloud.ibsnet.co.jp/api/search?zipcode=${r}`);if(!t.ok)throw new Error("郵便番号検索APIでエラーが発生しました");const e=await t.json();return e.status!==200?{success:!1,message:e.message||"郵便番号検索に失敗しました"}:!e.results||e.results.length===0?{success:!1,message:"該当する住所が見つかりませんでした"}:{success:!0,results:e.results}}catch(r){return console.error("Postal code lookup error:",r),{success:!1,message:"郵便番号検索中にエラーが発生しました"}}}function o(s){return`${s.address1}${s.address2}${s.address3}`}export{o as f,a as l};
